// src/pages/Login.jsx
import React, { useState } from 'react';
import Navbar from '../components/Navbar';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = (e) => {
    e.preventDefault();
    // Lógica de autenticación aquí (por ejemplo, enviar una solicitud POST al backend)
  };

  return (
    <div>
      <Navbar />
      <section className="text-center mt-12">
        <h1 className="text-3xl font-bold">Iniciar sesión</h1>
        <form onSubmit={handleLogin} className="max-w-md mx-auto mt-8">
          <input
            type="email"
            placeholder="Correo electrónico"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full p-2 mb-4 border border-gray-300 rounded"
          />
          <input
            type="password"
            placeholder="Contraseña"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full p-2 mb-4 border border-gray-300 rounded"
          />
          <button type="submit" className="w-full p-2 bg-blue-600 text-white rounded">Iniciar sesión</button>
        </form>
      </section>
    </div>
  );
};

export default Login;
