import React from 'react';

const AdminDashboard = () => {
  const role = localStorage.getItem('role');

  if (role !== 'administrador') {
    return (
      <div className="p-6 text-center">
        <h2 className="text-xl text-red-600 font-bold">Acceso denegado</h2>
        <p>No tienes permisos para acceder a esta sección.</p>
      </div>
    );
  }

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-4">Panel de Administración</h1>
      <p className="text-gray-700 mb-6">Bienvenido. Aquí puedes gestionar el sistema.</p>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div className="bg-white shadow p-4 rounded border">
          <h2 className="font-semibold mb-2">Usuarios</h2>
          <p>Accede a la gestión de usuarios, asignación de roles, etc.</p>
        </div>
        <div className="bg-white shadow p-4 rounded border">
          <h2 className="font-semibold mb-2">Productos</h2>
          <p>Agrega, edita o elimina productos del catálogo.</p>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
