// src/pages/AdminDashboard.jsx
import React from 'react';

const AdminDashboard = () => {
  const role = localStorage.getItem('role');

  if (role !== 'administrador') {
    return (
      <div className="p-6 text-center">
        <h2 className="text-2xl text-red-600 font-bold">⛔ Acceso denegado</h2>
        <p>No tienes permisos para acceder a esta sección.</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 py-10 px-6">
      <h1 className="text-4xl font-bold text-blue-800 mb-6">Panel de Administración</h1>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        <div className="bg-white rounded-xl shadow-md p-6 border-t-4 border-blue-600">
          <h2 className="text-xl font-semibold mb-2 text-blue-700">Gestionar Usuarios</h2>
          <p className="text-gray-600">Ver, crear y asignar roles a los usuarios del sistema.</p>
          <button className="mt-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
            Ir a Usuarios
          </button>
        </div>

        <div className="bg-white rounded-xl shadow-md p-6 border-t-4 border-green-500">
          <h2 className="text-xl font-semibold mb-2 text-green-700">Productos</h2>
          <p className="text-gray-600">Agrega, edita o elimina productos del catálogo.</p>
          <button className="mt-4 bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600">
            Ir a Productos
          </button>
        </div>

        <div className="bg-white rounded-xl shadow-md p-6 border-t-4 border-yellow-500">
          <h2 className="text-xl font-semibold mb-2 text-yellow-700">Promociones</h2>
          <p className="text-gray-600">Gestiona promociones, descuentos o campañas activas.</p>
          <button className="mt-4 bg-yellow-500 text-white px-4 py-2 rounded hover:bg-yellow-600">
            Ir a Promociones
          </button>
        </div>

        <div className="bg-white rounded-xl shadow-md p-6 border-t-4 border-purple-600">
          <h2 className="text-xl font-semibold mb-2 text-purple-700">Reportes</h2>
          <p className="text-gray-600">Genera reportes de ventas, usuarios, historial de acciones, etc.</p>
          <button className="mt-4 bg-purple-600 text-white px-4 py-2 rounded hover:bg-purple-700">
            Ver Reportes
          </button>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
